import React, { useContext} from 'react'
import { Link } from 'react-router-dom'
import './style.css'
import { AuthContext } from '../context/authContext.js'
import { useNavigate } from 'react-router-dom'
import { useCookies } from 'react-cookie'
const Navbar = () => {
  const {currentUser,logout,typeLogin,logoutGoogle}=useContext(AuthContext)
  const [cookies,setCookies]=useCookies(['access_token'])
  const navigate=useNavigate()
  const handleLogout=async(e)=>{
    e.preventDefault();
    if(typeLogin==="google")
    {
      
      window.open("http://localhost:8800/logout",'_self')
      await logoutGoogle()
      window.localStorage.removeItem('board')
    }
    else{
      await(logout());
      window.localStorage.removeItem('board')
      navigate('/auth/login')
    }
    
  }
  
  return (
    <div>
    <div className="nav">
        <div className="nav-items-left">
            <Link to='/'>
            <img src="https://kanban-app-jay.netlify.app/assets/logo-light.4fb0dd87.svg" alt=""/>
            </Link>
        </div>
        <div className='nav-items-right'>
          
          {cookies.access_token && 
          <>
          <h4>{currentUser.username}</h4>
          <img src={(typeLogin==="simple")?((currentUser.img)?process.env.PUBLIC_URL+"/upload/"+currentUser.img:"data:image/png;base64,"):currentUser.img} className="profile-pic" alt="profile"/>
          <button className='add-button logout' onClick={handleLogout}>Logout</button>
          </>
           }
        </div>
          
    </div>
    </div>
  )
}

export default Navbar